mapper.select:
	select t.* 
	from certificate t, certi_attr_rela r
	where t.info_id=r.info_id
   	
 		and t.ent_id = #{entId}
   	
   	     
   		and t.book_id = #{bookId}
   	
   	
   		and t.certi_state = #{certiState}
   	
   	
   		and r.attr_key = #{attrKey}
   	
   	
   		and r.attr_value = #{attrValue}
